const data = $input.first().json;
const webhookData = $('Webhook').first().json.body;

let videoUrl = '';
try {
  const rj = JSON.parse(data.data.resultJson);
  videoUrl = rj.resultUrls?.[0] || '';
} catch(e) {}

if (!videoUrl) {
  return [{json: {
    status: 'error',
    error_msg: 'No video in Kie.ai lip sync response: ' + JSON.stringify(data.data || {}).substring(0, 200),
    user_id: webhookData.user_id
  }}];
}

return [{json: {
  status: 'ok',
  video_url: videoUrl,
  user_id: webhookData.user_id
}}];

