const ADMIN_CHAT_ID = '5444227047';
const mode = 'lip_sync';
let errorMsg = 'Unknown error';
let userId = 'unknown';
try {
  const input = $input.first().json;
  errorMsg = input.error_msg || input.error || input.detail || JSON.stringify(input).substring(0, 200);
} catch(e) { errorMsg = 'Failed to parse error: ' + e.message; }
try { userId = $('Webhook').first().json.body.user_id || 'unknown'; } catch(e) {}
const text = '‚ö†Ô∏è Error in ' + mode + '\nUser: ' + userId + '\nError: ' + errorMsg;
try {
  await fetch('https://api.telegram.org/bot' + BOT_TOKEN + '/sendMessage', {
    method: 'POST', headers: {'Content-Type': 'application/json'},
    body: JSON.stringify({ chat_id: ADMIN_CHAT_ID, text: text })
  });
} catch(e) {}
return $input.all();

// Prepare data for Telegram HTTP Request nodes (no HTTP calls here)
const userId = $("Webhook").first().json.body.user_id;
const mediaUrl = $("Check Result").first().json.video_url;
const prompt = $("Webhook").first().json.body.prompt || '';
const topicRows = $('Get User Topic').all();
let threadId = null;
if (topicRows.length > 0 && topicRows[0].json.thread_id) {
  threadId = topicRows[0].json.thread_id;
}

const caption = 'üé¨ –í–∏–¥–µ–æ' + (prompt ? '\n\nüìù Prompt: ' + prompt.substring(0, 800) : '');

// Build send body
const sendBody = {
  chat_id: String(userId),
  video: mediaUrl,
  caption: caption
};
if (threadId) sendBody.message_thread_id = threadId;

return [{ json: {
  user_id: String(userId),
  mode: 'photo_to_video',
  thread_id: threadId,
  needs_topic: !threadId,
  send_body: JSON.stringify(sendBody),
  send_method: 'sendVideo',
  icon_color: 16766590,
  caption: caption,
  media_url: mediaUrl
} }];

// Pass through Prepare Send data with thread_id from newly created topic
const prepData = $("Prepare Send").first().json;
let newThreadId = null;
try {
  const createResult = $("Create Topic").first().json;
  newThreadId = createResult.result ? createResult.result.message_thread_id : null;
} catch(e) {}

return [{ json: {
  ...prepData,
  thread_id: newThreadId || prepData.thread_id
} }];

// Prepare Kie.ai request (lip-sync)
const body = $('Parse Input').first().json;

// Get URLs from Collect URLs node (S3 uploaded URLs), not from Parse Input
const collectData = $('Collect URLs').first().json;
const imageUrl = collectData.image_url || body.image_url;
const audioUrl = collectData.audio_url || body.audio_url;

return [{ json: {
  model: 'kling/ai-avatar-pro',
  input: {
    image_url: imageUrl,
    audio_url: audioUrl,
    prompt: body.prompt || ''
  }
} }];

