const data = $input.first().json;
const webhookData = $('Webhook').first().json.body;
let imageUrl = '';
try {
  const rj = JSON.parse(data.data.resultJson);
  imageUrl = rj.resultUrls?.[0] || '';
} catch(e) {}

if (!imageUrl) {
  return [{json: { status: 'error', error: 'generation_failed', error_msg: 'No image in Kie.ai response. State: ' + (data.data?.state || 'unknown'), user_id: webhookData.user_id }}];
}

return [{json: { status: 'ok', image_url: imageUrl, user_id: webhookData.user_id }}];

const BOT_TOKEN = '{{BOT_TOKEN}}';
const ADMIN_CHAT_ID = '-1003757993095';
const mode = 'text_to_image';
let errorMsg = 'Unknown error';
let userId = 'unknown';
try {
  const input = $input.first().json;
  errorMsg = input.error_msg || input.error || input.detail || input.message || JSON.stringify(input).substring(0, 300);
} catch(e) { errorMsg = 'Failed to parse: ' + e.message; }
try { userId = $('Webhook').first().json.body.user_id || 'unknown'; } catch(e) {}

// Send to error logging webhook (fire-and-forget)
try {
  await fetch('https://{{DOMAIN}}/webhook/log-error', {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify({
      severity: 'auto',
      category: 'generation',
      source_workflow: $workflow.name,
      source_endpoint: mode,
      source_node: $prevNode?.name || 'Error Logger',
      user_id: userId !== 'unknown' ? userId : null,
      error_message: errorMsg,
      error_code: '',
      request_data: { mode }
    })
  });
} catch(e) {}

// Also send to admin Telegram
const text = '\u26a0\ufe0f Error in ' + mode + '\n\n\ud83d\udc64 User: ' + userId + '\n\ud83d\udcdd ' + errorMsg.substring(0, 500);
try {
  await fetch('https://api.telegram.org/bot' + BOT_TOKEN + '/sendMessage', {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify({
      chat_id: ADMIN_CHAT_ID,
      text: text,
      parse_mode: 'HTML'
    })
  });
} catch(e) {}

return $input.all();

const userId = $("Webhook").first().json.body.user_id;
const mediaUrl = $("Check Result").first().json.image_url;
const prompt = $("Webhook").first().json.body.prompt || '';
const topicRows = $('Get User Topic').all();
let threadId = null;
if (topicRows.length > 0 && topicRows[0].json.thread_id) {
  threadId = topicRows[0].json.thread_id;
}

const caption = '\u{270F}\u{FE0F} \u0422\u0435\u043a\u0441\u0442 \u0432 \u0444\u043e\u0442\u043e' + (prompt ? '\n\n\u{1F4DD} Prompt: ' + prompt.substring(0, 800) : '');

const sendBody = {
  chat_id: String(userId),
  photo: mediaUrl,
  caption: caption
};
if (threadId) sendBody.message_thread_id = threadId;

return [{ json: {
  user_id: String(userId),
  mode: 'text_to_image',
  thread_id: threadId,
  needs_topic: !threadId,
  send_body: JSON.stringify(sendBody),
  send_method: 'sendPhoto',
  icon_color: 13338331,
  caption: caption,
  media_url: mediaUrl
} }];

// Pass through Prepare Send data with thread_id from newly created topic
const prepData = $("Prepare Send").first().json;
let newThreadId = null;
try {
  const createResult = $("Create Topic").first().json;
  newThreadId = createResult.result ? createResult.result.message_thread_id : null;
} catch(e) {}

return [{ json: {
  ...prepData,
  thread_id: newThreadId || prepData.thread_id
} }];

const translated = $('Translate Prompt').first().json.choices[0].message.content;
return [{ json: {
  model: 'flux-2/pro-text-to-image',
  input: {
    prompt: translated,
    aspect_ratio: '1:1',
    resolution: '2K'
  }
} }];

const data = items[0].json;
const state = data.data?.state || 'unknown';
const staticData = $getWorkflowStaticData('global');
const execId = $execution.id;
const key = 'poll_' + execId;
staticData[key] = (staticData[key] || 0) + 1;
const pollCount = staticData[key];
const MAX_POLLS = 18; // 18 x 10s = 3 min max

let status = 'continue';
if (state === 'success') status = 'success';
else if (state === 'fail' || pollCount >= MAX_POLLS) status = 'error';

return [{ json: { ...data, _poll_status: status, _poll_count: pollCount } }];

