=WITH commissions AS (
  SELECT * FROM apply_referral_commission({{ $json.ref_user_id }}, {{ $json.actual_cost }}, '{{ $json.ref_mode }}')
), ref_info AS (
  SELECT username FROM users WHERE id = {{ $json.ref_user_id }}
)
SELECT c.parent_id, c.level, c.commission, COALESCE(r.username, '') AS ref_username
FROM commissions c, ref_info r;

