const userId = $('Webhook').first().json.body.user_id;
const customPrompt = $('Webhook').first().json.body.prompt || null;
const photoUrls = $('Webhook').first().json.body.photos || [];

const NANOBANANA_API_KEY = 'e5e4877b659ed877497901f0c773b529';
const CALLBACK_URL = 'https://n8n.creativeanalytic.ru/webhook/nanobanana-callback';
const DEFAULT_PROMPT = "Professional high-quality portrait photo of this person, studio lighting, sharp focus, 8k resolution";

const prompt = customPrompt || DEFAULT_PROMPT;

if (!photoUrls || photoUrls.length < 2) {
    return [{ json: { success: false, error: "Минимум 2 фото требуется" } }];
}

try {
    const nanoBananaResp = await fetch(
        'https://api.nanobananaapi.ai/api/v1/nanobanana/generate-pro',
        {
            method: 'POST',
            headers: {
                'Authorization': `Bearer ${NANOBANANA_API_KEY}`,
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                prompt: prompt,
                imageUrls: photoUrls,
                resolution: "2K",
                aspectRatio: "1:1",
                callBackUrl: CALLBACK_URL
            })
        }
    );

    const taskData = await nanoBananaResp.json();

    if (taskData.code !== 200) {
        return [{ json: { success: false, error: taskData.message || taskData.msg || "NanoBanana API error" } }];
    }

    const taskId = taskData.data.taskId;

    await $pool.query(
        `INSERT INTO generation_tasks (user_id, task_id, prompt, status) VALUES ($1, $2, $3, $4)`,
        [userId, taskId, prompt, 'generating']
    );

    return [{ json: { success: true, taskId: taskId, userId: userId, photoCount: photoUrls.length, message: "Генерация запущена! Ожидайте результат через 30-60 секунд..." } }];

} catch (err) {
    return [{ json: { success: false, error: `NanoBanana API error: ${err.message}` } }];
}
