const body = items[0].json.body || items[0].json;

const code = body.code;
const msg = body.msg;
const taskId = body.data?.taskId;
const resultImageUrl = body.data?.info?.resultImageUrl;

if (!taskId) {
    return [{ json: { success: false, error: "Missing taskId" } }];
}

const taskResult = await $pool.query('SELECT user_id, prompt FROM generation_tasks WHERE task_id = $1', [taskId]);

if (taskResult.rows.length === 0) {
    return [{ json: { success: false, error: `Task not found: ${taskId}` } }];
}

const userId = taskResult.rows[0].user_id;
const prompt = taskResult.rows[0].prompt;

if (code === 200 && resultImageUrl) {
    await $pool.query(`UPDATE generation_tasks SET status = $1, result_url = $2, completed_at = NOW() WHERE task_id = $3`, ['completed', resultImageUrl, taskId]);
    await $pool.query('DELETE FROM user_photos WHERE user_id = $1', [userId]);

    return [{ json: { success: true, action: "send_result", userId: userId, resultImageUrl: resultImageUrl, message: "‚ú® –í–∞—à AI-–∞–≤–∞—Ç–∞—Ä –≥–æ—Ç–æ–≤!", prompt: prompt } }];
} else {
    await $pool.query(`UPDATE generation_tasks SET status = $1, error_message = $2, completed_at = NOW() WHERE task_id = $3`, ['failed', msg, taskId]);

    return [{ json: { success: false, action: "send_error", userId: userId, error: msg, message: "üòî –ì–µ–Ω–µ—Ä–∞—Ü–∏—è –Ω–µ —É–¥–∞–ª–∞—Å—å. –ü–æ–ø—Ä–æ–±—É–π—Ç–µ –µ—â—ë —Ä–∞–∑." } }];
}
